mapper.select:
SELECT
*
FROM index_deal_data
WHERE isvalid = 1

    AND index_code = #{indexCode}


    AND deal_period = #{dealPeriod}


    AND deal_time >= #{startDay}


    AND deal_time < #{endDay}

AND SUBSTR(deal_time ,6,2) = ${month}

SELECT SUBSTR(deal_time ,6,2) pointTime, SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) AS increaseNum , SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) AS decreaseNum , SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) AS totalNum , ROUND(SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) / ( SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) ) * 100 ,2) AS increaseRate , ROUND(SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) / ( SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) ) * 100 ,2) AS decreaseRate FROM index_deal_data WHERE isvalid = 1
AND index_code = #{indexCode}
AND deal_period = #{dealPeriod}
AND deal_time >= #{startDay}
AND deal_time < #{endDay}
GROUP BY SUBSTR(deal_time ,6,2)

SELECT SUBSTR(deal_time ,6,2) pointTime, SUM(close_price - open_price) changeRange, SUM(high_price - low_price) shockRange , ROUND(SUM(close_price - open_price) / SUM(1),2) changeAverage, ROUND(SUM(high_price - low_price) / SUM(1),2) shockAverage , ROUND(SUM(open_price) / SUM(1),2) openAveragePrice, ROUND(SUM(close_price) / SUM(1),2) closeAveragePrice, ROUND(SUM(high_price) / SUM(1),2) highAveragePrice, ROUND(SUM(low_price) / SUM(1),2) lowAveragePrice FROM index_deal_data WHERE isvalid = 1
AND index_code = #{indexCode}
AND deal_period = #{dealPeriod}
AND deal_time >= #{startDay}
AND deal_time < #{endDay}
GROUP BY SUBSTR(deal_time ,6,2)

SELECT ((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1) pointTime, SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) AS increaseNum , SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) AS decreaseNum , SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) AS totalNum , ROUND(SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) / ( SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) ) * 100 ,2) AS increaseRate , ROUND(SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) / ( SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) ) * 100 ,2) AS decreaseRate FROM index_deal_data WHERE isvalid = 1 AND deal_period = 2
AND index_code = #{indexCode}
AND deal_time >= #{startDay}
AND deal_time < #{endDay}
AND SUBSTR(deal_time ,6,2) = ${month} GROUP BY ((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1)

SELECT SUBSTR(deal_time ,6,2) months, ((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1) weeks, SUM(close_price - open_price) changeRange, SUM(high_price - low_price) shockRange , ROUND(SUM(close_price - open_price) / SUM(1),2) changeAverage, ROUND(SUM(high_price - low_price) / SUM(1),2) shockAverage FROM index_deal_data WHERE isvalid = 1 AND deal_period = 2
AND index_code = #{indexCode}
AND deal_time >= #{startDay}
AND deal_time < #{endDay}
AND SUBSTR(deal_time ,6,2) = ${month} GROUP BY ((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1)

SELECT ((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1) pointTime, SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) AS increaseNum , SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) AS decreaseNum , SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) AS totalNum , ROUND(SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) / ( SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) ) * 100 ,2) AS increaseRate , ROUND(SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) / ( SUM(CASE WHEN close_price - open_price > 0 THEN 1 ELSE 0 END) + SUM(CASE WHEN close_price - open_price < 0 THEN 1 ELSE 0 END) ) * 100 ,2) AS decreaseRate FROM index_deal_data WHERE isvalid = 1 AND deal_period = 1
AND index_code = #{indexCode}
AND deal_time >= #{startDay}
AND deal_time < #{endDay}
AND((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1) = ${week} GROUP BY (DAYOFWEEK(deal_time) -1)

SELECT SUBSTR(deal_time ,6,2) months, ((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1) weeks, SUM(close_price - open_price) changeRange, SUM(high_price - low_price) shockRange , ROUND(SUM(close_price - open_price) / SUM(1),2) changeAverage, ROUND(SUM(high_price - low_price) / SUM(1),2) shockAverage FROM index_deal_data WHERE isvalid = 1 AND deal_period = 1
AND index_code = #{indexCode}
AND deal_time >= #{startDay}
AND deal_time < #{endDay}
AND((DAY(deal_time)+WEEKDAY(deal_time-INTERVAL DAY(deal_time) DAY)) DIV 7 + 1) = ${week} GROUP BY (DAYOFWEEK(deal_time) -1)
