IFNULL(ROUND(a.oneUpper/(a.oneRise + a.oneDown),2),0) oneUpperAverage , IFNULL(ROUND(a.towUpper/(a.towRise + a.towDown),2),0) towUpperAverage , IFNULL(ROUND(a.threeUpper/(a.threeRise + a.threeDown),2),0) threeUpperAverage , IFNULL(ROUND(a.fourUpper/(a.fourRise + a.fourDown),2),0) fourUpperAverage , IFNULL(ROUND(a.fiveUpper/(a.fiveRise + a.fiveDown),2),0) fiveUpperAverage , IFNULL(ROUND(a.sixUpper/(a.sixRise + a.sixDown),2),0) sixUpperAverage ,
IFNULL(ROUND(a.oneShock/(a.oneRise + a.oneDown),2),0) oneShockAverage , IFNULL(ROUND(a.towShock/(a.towRise + a.towDown),2),0) towShockAverage , IFNULL(ROUND(a.threeShock/(a.threeRise + a.threeDown),2),0) threeShockAverage , IFNULL(ROUND(a.fourShock/(a.fourRise + a.fourDown),2),0) fourShockAverage , IFNULL(ROUND(a.fiveShock/(a.fiveRise + a.fiveDown),2),0) fiveShockAverage , IFNULL(ROUND(a.sixShock/(a.sixRise + a.sixDown),2),0) sixShockAverage FROM( SELECT SUBSTR(d.deal_time ,6,2) pointTime, ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) weeks, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS oneRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS oneDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS towRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS towDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS threeRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS threeDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fourRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fourDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fiveRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fiveDown , SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS sixRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS sixDown ,
SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN d.close_price - d.open_price ELSE 0 END) AS oneUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN d.close_price - d.open_price ELSE 0 END) AS towUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN d.close_price - d.open_price ELSE 0 END) AS threeUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN d.close_price - d.open_price ELSE 0 END) AS fourUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN d.close_price - d.open_price ELSE 0 END) AS fiveUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN d.close_price - d.open_price ELSE 0 END) AS sixUpper,
SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS oneShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS towShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS threeShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS fourShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS fiveShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS sixShock FROM index_deal_data d WHERE d.deal_period = 2
AND d.`index_name` REGEXP #{indexName}
AND d.deal_time >= #{startDay}
AND d.deal_time < #{endDay}
GROUP BY SUBSTR(d.deal_time ,6,2) ) a

SELECT a.pointTime , IFNULL(ROUND(a.oneRise/(a.oneRise + a.oneDown) * 100,2),0) oneRiseRate , IFNULL(ROUND(a.towRise/(a.towRise + a.towDown) * 100,2),0) towRiseRate , IFNULL(ROUND(a.threeRise/(a.threeRise + a.threeDown) * 100,2),0) threeRiseRate , IFNULL(ROUND(a.fourRise/(a.fourRise + a.fourDown) * 100,2),0) fourRiseRate , IFNULL(ROUND(a.fiveRise/(a.fiveRise + a.fiveDown) * 100,2),0) fiveRiseRate , IFNULL(ROUND(a.sixRise/(a.sixRise + a.sixDown) * 100,2),0) sixRiseRate ,
IFNULL(ROUND(a.oneUpper/(a.oneRise + a.oneDown),2),0) oneUpperAverage , IFNULL(ROUND(a.towUpper/(a.towRise + a.towDown),2),0) towUpperAverage , IFNULL(ROUND(a.threeUpper/(a.threeRise + a.threeDown),2),0) threeUpperAverage , IFNULL(ROUND(a.fourUpper/(a.fourRise + a.fourDown),2),0) fourUpperAverage , IFNULL(ROUND(a.fiveUpper/(a.fiveRise + a.fiveDown),2),0) fiveUpperAverage , IFNULL(ROUND(a.sixUpper/(a.sixRise + a.sixDown),2),0) sixUpperAverage ,
IFNULL(ROUND(a.oneShock/(a.oneRise + a.oneDown),2),0) oneShockAverage , IFNULL(ROUND(a.towShock/(a.towRise + a.towDown),2),0) towShockAverage , IFNULL(ROUND(a.threeShock/(a.threeRise + a.threeDown),2),0) threeShockAverage , IFNULL(ROUND(a.fourShock/(a.fourRise + a.fourDown),2),0) fourShockAverage , IFNULL(ROUND(a.fiveShock/(a.fiveRise + a.fiveDown),2),0) fiveShockAverage , IFNULL(ROUND(a.sixShock/(a.sixRise + a.sixDown),2),0) sixShockAverage FROM( SELECT SUBSTR(d.deal_time ,6,2) pointTime, ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) weeks, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS oneRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS oneDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS towRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS towDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS threeRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS threeDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fourRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fourDown, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fiveRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS fiveDown , SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN (CASE WHEN d.close_price - d.open_price > 0 THEN 1 ELSE 0 END) ELSE 0 END) AS sixRise, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN (CASE WHEN d.close_price - d.open_price < 0 THEN 1 ELSE 0 END) ELSE 0 END) AS sixDown ,
SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN d.close_price - d.open_price ELSE 0 END) AS oneUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN d.close_price - d.open_price ELSE 0 END) AS towUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN d.close_price - d.open_price ELSE 0 END) AS threeUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN d.close_price - d.open_price ELSE 0 END) AS fourUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN d.close_price - d.open_price ELSE 0 END) AS fiveUpper, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN d.close_price - d.open_price ELSE 0 END) AS sixUpper,
SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 1 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS oneShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 2 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS towShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 3 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS threeShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 4 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS fourShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 5 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS fiveShock, SUM(CASE WHEN ((DAY(d.deal_time)+WEEKDAY(d.deal_time-INTERVAL DAY(d.deal_time) DAY)) DIV 7 + 1) = 6 THEN d.`high_price` - d.`low_price` ELSE 0 END) AS sixShock FROM stock_company_sector s LEFT JOIN stock_company_base c ON s.`stock_code` = c.`stock_a_code` LEFT JOIN stock_deal_data d ON s.`stock_code` = d.`stock_code` WHERE LEFT(s.`stock_code`,2) != 68
AND d.`deal_period` = #{dealPeriod}
AND s.`forth_sector` = #{sectorName}
AND s.`stock_code` = #{stockCode}
AND d.deal_time >= #{startDay}
AND d.deal_time < #{endDay}
AND c.`publish_date` <= #{startDay}
GROUP BY SUBSTR(d.deal_time ,6,2) ) a
