mapper.select:
SELECT b.`stock_a_code` stockCode, b.`stock_a_name` stockName, b.`stock_plate` stockPlate, b.`company_region` companyRegion, b.`middle_contry_sector` middleContrySector, b.`stock_sector` stockSector, b.`establish_date` establishDate, b.`company_regist_money` companyRegistMoney, b.`publish_date` publishDate, b.`publish_amount` publishAmount FROM stock_company_base b LEFT JOIN stock_company_notice n ON b.`stock_a_code` = n.`stock_code` WHERE b.`stock_plate_class` = 'A' AND b.`stock_plate_level` < 3
AND b.`stock_a_code` REGEXP #{stockCode}
AND b.`stock_a_name` REGEXP #{stockName}
AND n.`publish_time` >= #{startDay}
AND n.`publish_time` < #{endDay}
GROUP BY b.`stock_a_code` ORDER BY b.`stock_a_code`,n.`big_notice_class`

SELECT n.`publish_time` publishTime, IFNULL(c.`class_level`,1) classLevel, n.`big_notice_class` bigNoticeClass, n.`middle_notice_class` middleNoticeClass, n.`notice_type` noticeType, n.`notice_title` noticeTitle , n.`notice_detail` noticeDetail FROM stock_company_notice n LEFT JOIN stock_company_notice_class c ON n.`class_code` = c.`class_code` WHERE n.`stock_code` = #{stockCode}
AND n.`publish_time` >= #{startDay}
AND n.`publish_time` < #{endDay}
ORDER BY n.`publish_time`,n.`big_notice_class`,n.`middle_notice_class` ,c.`class_level`
